package week2.day2;

public class Characteroccurance {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String input ="welcome to chennai";
		
		int count = 0 ;
		
		for (int i = 0; i < input.length(); i++) {
			
			if (input.charAt(i)=='e') {
				
				
				count++;
				
				
				
			}
		}
			System.out.println(count);
			
		

	}

}
